#function
def Netflix_Recommender(Age):
    global type
    global genre
    global title
    global rating
    global filtered_list

    #print("Welcome to Netflix Recommender")
    while True:
        question = input("Do you want something recommeneded to watch?:")
        if question == "Yes" or question == "yes" or question == "yeah":
            print("Lets get started")
        else:
            print("Thank you for stopping by goodbye")
            break

        if Age > 10 and Age < 14:
            for i in range(len(rating)):
                if rating[i] == "TV-PG":
                    filtered_list.append(genre[i]) #tv pg

        elif Age < 6 and Age > 2:    #tv y
            for i in range(len(rating)):
                if rating[i] == "TV-Y":
                    filtered_list.append(genre[i])

        elif Age > 14 and Age < 17: #tv-14
            for i in range(len(rating)):
                if rating[i] == "TV-14":
                    filtered_list.append(genre[i])

        elif Age >= 17:  #tv-ma and R
            for i in range(len(rating)):
                if rating[i] == "TV-MA" and rating[i] == "R":
                    filtered_list.append(genre[i])

        elif Age > 7 and Age < 10: #tv-y7
            for i in range(len(rating)):
                if rating[i] == "TV-Y7":
                    filtered_list.append(genre[i])

        elif Age == 13: #pg-13
            for i in range(len(rating)):
                if rating[i] == "PG-13":
                    filtered_list.append(genre[i])

        elif Age < 7: #tv g and nr
            for i in range(len(rating)):
                if rating[i] == "NR" and rating[i] == "TV-G":
                    filtered_list.append(genre[i])



        ans1 = input("Do you want to watch a movie or Tv or a show?: ") # asking for type
        if ans1 == "movie" or ans1 == "Movie":
            for i in range(len(type)):
                if type[i] == "Movie":
                    filtered_list.append(genre[i])
            ans3 = input("What type of genre do you want to watch?")
            if str(ans3) in genre:
                rec = genre.index(ans3)
                print(title[rec])
            else:
                print("Sorry we couldn't find anthing")


        elif ans1 == "Tv Show" or ans1 == "TV show" or ans1 == "tv show" or ans1 == "Tv":
            for i in range (len(type)):
                if type[i] == "TV-Show":
                    filtered_list.append(genre[i])
            ans3 = input("What type of genre do you want to watch?")
            if str(ans3) in genre:
                rec = genre.index(ans3)
                print(title[rec])
            else:
                print("Sorry we couldn't find anthing")


